#strict 2#appendto CLNKprivate func Chopping() {	if(_inherited()) {		var tree = GetActionTarget();		var rest = Max(tree->GetComponent(WOOD), 1);		var shrinkage = tree->GetCon() / rest;		var oldHeight = tree->GetObjHeight();		CreateContents(WOOD);		if(rest == 1) {			tree->RemoveObject();			FinishCommand(this, true);		} else {			tree->DoCon(-shrinkage);			var verticalAdjustment = (oldHeight - tree->GetObjHeight()) / 3;			tree->SetPosition(tree->GetX(), tree->GetY() + verticalAdjustment);		}	}	return true;}